import WaveSurfer from"https://cdn.jsdelivr.net/npm/wavesurfer.js@7/dist/wavesurfer.esm.js";const wavesurfer=WaveSurfer.create({container:"#waveform",waveColor:"#0c587eff",progressColor:"#fff6c8",normalize:!0,responsive:!0});window.wavesurfer=wavesurfer;const hover=document.querySelector("#hover"),waveform=document.querySelector("#waveform");waveform&&hover&&waveform.addEventListener("pointermove",(e=>{hover.style.width=`${e.offsetX}px`}));export default wavesurfer;